import { Injectable } from '@angular/core';
import { Observable, Subject } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class ProductosService {

  // $ indica que es un observable
  private productos: any[] = [
    {id: 1, descripcion: 'MacBook Air', precio: 12000, disponibilidad: 10,
    url: 'data:image/jpeg;base64,'},
    {id: 2, descripcion: 'Teclado VSG Quasar', precio: 899, disponibilidad: 8,
    url: 'data:image/jpeg;base64,'},
    {id: 3, descripcion: 'Audifonos Soundcore Q30', precio: 1699, disponibilidad: 31,
    url: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTZarrnB3Ypw7NHhta1NVfWhqMxQJuOj4Q7Fg&usqp=CAU'},
    {id: 4, descripcion: 'Mouse Logitech MX Master 3', precio: 2135.50, disponibilidad: 3,
    url: 'data:image/jpeg;base64,'}
  ];

  private carrito: Map<any, number> = new Map<any, number>();
  private total: number = 0;

  private $prodSeleccionado = new Subject<any>();

  constructor() { }

  agregarProducto(producto: any) {
    this.productos.push(producto);
  }

  getProductos(): any[] {
    return this.productos;
  }

  modificarProducto(producto: any) {
    this.productos.forEach(p => {
      if (p.id === producto.id) {
        p.descripcion = producto.descripcion;
        p.precio = producto.precio;
        p.disponibilidad = producto.disponibilidad;
        p.url = producto.url;
      }
    })
  }

  eliminarProducto(id: number) {
    this.productos.forEach(p => {
      if (p.id === id) {
        this.productos.splice(this.productos.indexOf(p), 1);
      }
    })
  }

  actualizarID(id: number) {
    this.productos.forEach(p => {if (p.id > id) p.id--});
  }

  seleccionarProducto(producto: any) {
    this.$prodSeleccionado.next(producto);
  }

  getProdSeleccionado(): Observable<any> {
    return this.$prodSeleccionado.asObservable();
  }

  agregarAlCarrito(producto: any): void {
    let cant = this.carrito.get(producto);
    if (this.carrito.has(producto) && cant != undefined) {
      cant++;
      this.carrito.set(producto, cant);
    } else {
      this.carrito.set(producto, 1);
    }
    this.total += producto.precio;
  }

  quitarDelCarrito(producto: any): void {
    let cant = this.carrito.get(producto);
    if (cant != undefined) {
      this.total -= producto.precio * cant;
    }
    this.carrito.delete(producto);
  }

  getCarrito(): Map<any, number> {
    return this.carrito;
  }

  getTotal(): number {
    return this.total;
  }

  getCantDeProducto(producto: any): number {
    let cant = this.carrito.get(producto);
    if (cant != undefined) {
      return cant;
    }
    return 1;
  }

}
